mapper.select:
select * from task_list
where project_seq = #{project_seq}
order by id

mapper.insert:
update task_card
set title= #{title},
description=#{description}, dueDate=#{dueDate}
where id=#{id}

update task_list
set title= #{title}
where id=#{id}

update task_list
set defaultStyle= #{defaultStyle}
where id=#{id}

update task_card
set done= #{done}
where id=#{id}

update task_card
set listId= #{listId},
cardIndex=#{cardIndex}
where id=#{id}
